const hourHand=document.getElementById("hour"),minuteHand=document.getElementById("minute"),secondHand=document.getElementById("second"),background=document.querySelector(".rotatingbg");function setTime(){const e=new Date,t=e.getHours(),n=e.getUTCMinutes(),o=360*e.getUTCSeconds()/60,d=360*n/60+o/60,r=360*t/12+d/12;secondHand.style.transform=`rotate(${o}deg)`,minuteHand.style.transform=`rotate(${d}deg)`,hourHand.style.transform=`rotate(${r}deg)`,background.style.transform=`rotate(${o}deg)`,background.parentNode.style.opacity=1}setInterval(setTime,1e3);